mapper.insert:
INSERT INTO PRODUCT (
CATEGORY_SEQ
, USER_SEQ
, PRODUCT_NAME
, PRODUCT_PRICE
, PRODUCT_STATUS
, PRODUCT_AMOUNT
, PRODUCT_WEIGHT )
VALUES (
#{categorySeq}
, #{userSeq}
, #{productName}
, #{productPrice}
, #{productStatus}
, #{productAmount}
, #{productWeight} )

mapper.update:
UPDATE PRODUCT
SET PRODUCT_NAME = #{ productName }
, PRODUCT_PRICE = #{ productPrice }
, PRODUCT_AMOUNT = #{ productAmount }
, PRODUCT_WEIGHT = #{ productWeight }
, CATEGORY_SEQ = #{ categorySeq }
WHERE PRODUCT_SEQ = #{ productSeq }
AND USER_SEQ = #{ userSeq }

mapper.select:
SELECT CATEGORY_SEQ
FROM CATEGORY
WHERE CATEGORY_NAME = #{ categoryName }

mapper.update:
UPDATE CATEGORY
SET CATEGORY_NAME = #{ categoryName }
WHERE CATEGORY_SEQ = #{ categorySeq }

mapper.delete:
DELETE
FROM PRODUCT
WHERE PRODUCT_SEQ = #{ productSeq }
