update.foreach:
UPDATE
    sector
SET
volume = volume - #{item.productAmount}
WHERE
sector_seq = #{item.sectorSeq}

mapper.select:
SELECT p.product_seq, p.category_seq, c.category_name, s.sector_name, g.grade_name, u.name, p.product_name, p.product_price, p.product_status, p.register_date, p.update_date, p.product_amount FROM product p inner join category c on p.category_seq = c.category_seq inner join sector s on p.sector_seq = s.sector_seq inner join grade g on p.grade_seq = g.grade_seq inner join user u on p.user_seq = u.user_seq WHERE p.product_status = #{productStatus}
AND p.user_seq = #{userSeq}
AND p.product_name LIKE CONCAT('%',#{productName},'%')
